// Extract data from Vapi tool call
const body = $input.first().json.body || $input.first().json;

// Vapi sends tool calls in this format
let args = {};

// Handle different Vapi payload formats
if (body.message && body.message.toolCallList) {
  args = body.message.toolCallList[0].function.arguments;
} else if (body.message && body.message.toolCalls) {
  const toolCall = body.message.toolCalls[0];
  args = typeof toolCall.function.arguments === 'string' 
    ? JSON.parse(toolCall.function.arguments) 
    : toolCall.function.arguments;
} else if (body.toolCallList) {
  args = body.toolCallList[0].function.arguments;
} else if (body.recipient_email) {
  // Direct payload (testing)
  args = body;
} else {
  // Fallback: try to find args anywhere in the payload
  args = body;
}

const now = new Date();
const dateStr = now.toLocaleDateString('en-US', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

return [{
  json: {
    email: args.recipient_email || '',
    name: args.recipient_name || 'there',
    company: args.company_name || 'your company',
    action: args.action_type || 'send_info',
    meeting_date: args.meeting_date || '',
    meeting_time: args.meeting_time || '',
    pain_points: args.pain_points || '',
    summary: args.call_summary || '',
    timestamp: dateStr
  }
}];

// Smart Date Parser + Calendar Formatter
const allData = $('Extract Call Data').first().json;
const dateStr = (allData.meeting_date || '').toLowerCase().trim();
const timeStr = (allData.meeting_time || '').toLowerCase().trim();

if (!dateStr && !timeStr) {
  return [{ json: { ...allData, calendar_ready: false } }];
}

try {
  const now = new Date();
  let targetDate = new Date(now);
  const days = ['sunday','monday','tuesday','wednesday','thursday','friday','saturday'];
  let foundDay = false;

  for (let i = 0; i < days.length; i++) {
    if (dateStr.includes(days[i])) {
      let diff = i - now.getDay();
      if (diff <= 0) diff += 7;
      if (dateStr.includes('next')) diff += 7;
      targetDate.setDate(now.getDate() + diff);
      foundDay = true;
      break;
    }
  }
  if (!foundDay && dateStr.includes('tomorrow')) { targetDate.setDate(now.getDate() + 1); foundDay = true; }
  if (!foundDay && dateStr.includes('today')) { foundDay = true; }
  if (!foundDay) {
    const parsed = new Date(dateStr);
    if (!isNaN(parsed.getTime()) && parsed.getFullYear() > 2020) { targetDate = parsed; foundDay = true; }
    else {
      const mm = dateStr.match(/(jan|feb|mar|apr|may|jun|jul|aug|sep|oct|nov|dec)\w*\s+(\d{1,2})/);
      if (mm) {
        const months = {jan:0,feb:1,mar:2,apr:3,may:4,jun:5,jul:6,aug:7,sep:8,oct:9,nov:10,dec:11};
        targetDate = new Date(now.getFullYear(), months[mm[1].substring(0,3)], parseInt(mm[2]));
        if (targetDate < now) targetDate.setFullYear(now.getFullYear() + 1);
        foundDay = true;
      }
    }
  }
  if (!foundDay) {
    let add = 1;
    if (now.getDay() === 5) add = 3;
    if (now.getDay() === 6) add = 2;
    targetDate.setDate(now.getDate() + add);
  }

  let hours = 14, minutes = 0;
  const combined = dateStr + ' ' + timeStr;
  const tm = combined.match(/(\d{1,2})(?::(\d{2}))?\s*(am|pm)?/i);
  if (tm) {
    hours = parseInt(tm[1]);
    minutes = tm[2] ? parseInt(tm[2]) : 0;
    const p = (tm[3] || '').toLowerCase();
    if (p === 'pm' && hours < 12) hours += 12;
    if (p === 'am' && hours === 12) hours = 0;
    if (!p && hours >= 1 && hours <= 7) hours += 12;
  }

  targetDate.setHours(hours, minutes, 0, 0);
  const endDate = new Date(targetDate);
  endDate.setMinutes(endDate.getMinutes() + 20);

  const fmt = (d) => {
    const p = (n) => String(n).padStart(2, '0');
    // Include EST timezone offset so Google Calendar creates event in Toronto time
    return d.getFullYear() + '-' + p(d.getMonth()+1) + '-' + p(d.getDate()) + 'T' + p(d.getHours()) + ':' + p(d.getMinutes()) + ':00';
  };

  // Pre-format calendar fields so Calendar node uses simple expressions
  const eventTitle = 'Demo: ' + (allData.company || 'Prospect') + ' - AI Operations Platform';
  const eventDesc = 'Live demo of AI shift-filling and operations automation.\n\nAttendee: ' + (allData.name || '') + ' (' + (allData.company || '') + ')\nEmail: ' + (allData.email || '') + '\nPain Points: ' + (allData.pain_points || 'N/A') + '\nCall Summary: ' + (allData.summary || 'N/A');

  return [{ json: {
    ...allData,
    start_datetime: fmt(targetDate),
    end_datetime: fmt(endDate),
    timezone: 'America/Toronto',
    event_title: eventTitle,
    event_description: eventDesc,
    calendar_ready: true
  }}];
} catch (err) {
  return [{ json: { ...allData, calendar_ready: false, parse_error: String(err) } }];
}

