// Filter articles from last 24 hours and remove duplicates
const HOURS = 24;
const now = new Date();
const cutoff = new Date(now.getTime() - HOURS * 60 * 60 * 1000);

// Track seen titles to remove duplicates
const seenTitles = new Set();

const filtered = items.filter(item => {
  const rawDate = item.json.pubDate || item.json.isoDate || item.json.published || item.json.date;

  if (!rawDate) return true; // Keep if no date (rare)

  const articleDate = new Date(rawDate);
  if (isNaN(articleDate.getTime())) return true;

  // Check if within last 24 hours
  if (articleDate < cutoff) return false;

  // Check for duplicates by title
  const title = (item.json.title || '').toLowerCase().trim().substring(0, 50);
  if (seenTitles.has(title)) return false;
  seenTitles.add(title);

  return true;
});

// Sort by date (newest first)
filtered.sort((a, b) => {
  const dateA = new Date(a.json.pubDate || a.json.isoDate || 0);
  const dateB = new Date(b.json.pubDate || b.json.isoDate || 0);
  return dateB - dateA;
});

return filtered.length > 0 ? filtered : [{ json: { noArticles: true } }];

// Extract clean article data
const item = $input.item.json;

// Clean description/content - remove HTML tags
let description = item.content || item.contentSnippet || item.description || item.summary || '';
description = description.replace(/<[^>]*>/g, '').replace(/&[^;]+;/g, ' ').substring(0, 500).trim();

// Determine source from URL
const link = item.link || item.url || '';
let source = 'News';
if (link.includes('techcrunch')) source = 'TechCrunch';
else if (link.includes('theverge')) source = 'The Verge';
else if (link.includes('arstechnica')) source = 'Ars Technica';
else if (link.includes('ycombinator') || link.includes('hnrss')) source = 'Hacker News';
else if (link.includes('google.com')) source = 'Google News';

// Format date nicely
let pubDate = item.pubDate || item.isoDate || item.published || item.date || '';
try {
  const d = new Date(pubDate);
  pubDate = d.toLocaleString('en-IN', { 
    timeZone: 'Asia/Kolkata',
    day: 'numeric',
    month: 'short',
    hour: '2-digit',
    minute: '2-digit'
  });
} catch (e) {
  pubDate = 'Recent';
}

return {
  title: (item.title || 'Untitled Article').substring(0, 200),
  description: description || 'No description available.',
  link: link,
  source: source,
  pubDate: pubDate
};

