const items = $input.all();

let newsText = "Here are today's AI news articles:\n\n";

items.forEach((item, index) => {
  const title = item.json.title || 'No title';
  const link = item.json.link || item.json.url || 'No link';
  const pubDate = item.json.pubDate || item.json.isoDate || 'No date';
  const description = item.json.contentSnippet || item.json.description || item.json.content || 'No description';

  newsText += `${index + 1}. Title: ${title}\n`;
  newsText += `   Link: ${link}\n`;
  newsText += `   Date: ${pubDate}\n`;
  newsText += `   Summary: ${description.substring(0, 300)}...\n\n`;
});

return [{ json: { newsCollection: newsText, itemCount: items.length } }];

