const input = $input.first().json;

const today = new Date().toLocaleDateString('en-US', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

// Parse the AI output
let topNews = [];
let overallSummary = '';

try {
  if (input.topNews) {
    topNews = input.topNews;
  }
  if (input.overallSummary) {
    overallSummary = input.overallSummary;
  }
} catch (e) {
  // If parsing fails, use raw output
  overallSummary = 'AI news digest for today.';
}

// Format for Email (HTML)
let emailHtml = `
<html>
<body style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto; padding: 20px;">
  <h1 style="color: #2563eb;">🤖 Daily AI News Digest</h1>
  <p style="color: #6b7280; font-size: 14px;">${today}</p>
  <hr style="border: 1px solid #e5e7eb;">

  <p style="font-style: italic; color: #4b5563; background: #f3f4f6; padding: 15px; border-radius: 8px;">
    ${overallSummary}
  </p>

  <h2 style="color: #1f2937;">📰 Top 5 AI News Stories</h2>
`;

topNews.forEach((news, index) => {
  emailHtml += `
  <div style="margin-bottom: 25px; padding: 15px; border-left: 4px solid #2563eb; background: #f9fafb;">
    <h3 style="margin: 0 0 10px 0; color: #1f2937;">
      #${news.rank || index + 1} - ${news.headline || 'Untitled'}
    </h3>
    <p style="color: #4b5563; margin: 0 0 10px 0;">${news.summary || 'No summary available.'}</p>
    <p style="margin: 0;">
      <a href="${news.link || '#'}" style="color: #2563eb; text-decoration: none;">Read full article →</a>
      <span style="color: #9ca3af; margin-left: 15px;">Importance: ${news.importanceScore || 'N/A'}/10</span>
    </p>
  </div>
  `;
});

emailHtml += `
  <hr style="border: 1px solid #e5e7eb;">
  <p style="color: #9ca3af; font-size: 12px; text-align: center;">
    Generated by your n8n AI News Workflow<br>
    Powered by AI curation and RSS feeds
  </p>
</body>
</html>
`;

// Format for Telegram (Markdown)
let telegramText = `🤖 *Daily AI News Digest*\n📅 ${today}\n\n`;
telegramText += `💡 _${overallSummary}_\n\n`;
telegramText += `━━━━━━━━━━━━━━━━━━\n\n`;

topNews.forEach((news, index) => {
  telegramText += `*#${news.rank || index + 1} - ${(news.headline || 'Untitled').replace(/[*_`]/g, '')}*\n\n`;
  telegramText += `${(news.summary || 'No summary').replace(/[*_`]/g, '')}\n\n`;
  telegramText += `🔗 [Read more](${news.link || '#'}) | ⭐ ${news.importanceScore || 'N/A'}/10\n\n`;
  telegramText += `━━━━━━━━━━━━━━━━━━\n\n`;
});

telegramText += `_Powered by n8n AI News Workflow_`;

// Email subject
const emailSubject = `🤖 Daily AI News Digest - ${today}`;

return [{
  json: {
    emailSubject: emailSubject,
    emailHtml: emailHtml,
    telegramText: telegramText,
    rawData: { topNews, overallSummary }
  }
}];
