// Determine time of day (IST = UTC + 5:30)
const now = new Date();
const utcH = now.getUTCHours();
const utcM = now.getUTCMinutes();
const totalMin = utcH * 60 + utcM + 330;
const istHour = Math.floor((totalMin % 1440) / 60);

let message = '';
let timeSlot = '';

if (istHour >= 9 && istHour <= 11) {
  timeSlot = 'morning';
  message = "Good morning Dhruv! Quick check-in from Angelina. Here's your morning push. Focus on your top 3 priorities today. Close those deals, create content, and keep building the pipeline. You've got the skills, now execute. Let's make today count! Have a great one.";
} else if (istHour >= 13 && istHour <= 15) {
  timeSlot = 'afternoon';
  message = "Hey Dhruv, afternoon check-in! How's the progress? Remember, outreach first, then build. Don't get stuck in perfection. Ship it, test it, improve it. Check your pipeline, follow up on pending leads, and keep the momentum going. Back to work, you got this!";
} else {
  timeSlot = 'evening';
  message = "Evening Dhruv! Hope you crushed it today. Before you wrap up, review what you accomplished, celebrate the wins, and set your top 3 priorities for tomorrow. Rest well tonight, recharge, and come back stronger. Angelina out, see you tomorrow morning!";
}

// Build TwiML for Twilio direct call
const twiml = `<Response><Say voice="Polly.Joanna" language="en-US">${message}</Say><Pause length="1"/><Say voice="Polly.Joanna">Talk soon Dhruv!</Say></Response>`;

return [{ json: { message, timeSlot, twiml, timestamp: now.toISOString() } }];

