// Get top 3 latest AI news items
const items = $input.all().slice(0, 3);

const newsItems = items.map((item, index) => {
  const data = item.json;
  return {
    title: data.title || 'No title',
    link: data.link || '#',
    description: (data.contentSnippet || data.description || 'No description').substring(0, 200) + '...',
    pubDate: data.pubDate || data.isoDate || 'Unknown date'
  };
});

// Build HTML email
const today = new Date().toLocaleDateString('en-US', { 
  weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' 
});

let emailHtml = `
<div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
  <h1 style="color: #2563eb; border-bottom: 2px solid #2563eb; padding-bottom: 10px;">
    🤖 Daily AI News
  </h1>
  <p style="color: #666; font-size: 14px;">${today}</p>
`;

newsItems.forEach((news, i) => {
  emailHtml += `
  <div style="margin: 20px 0; padding: 15px; background: #f8fafc; border-radius: 8px; border-left: 4px solid #2563eb;">
    <h2 style="margin: 0 0 10px 0; font-size: 16px;">
      <a href="${news.link}" style="color: #1e40af; text-decoration: none;">${i + 1}. ${news.title}</a>
    </h2>
    <p style="margin: 0; color: #475569; font-size: 14px; line-height: 1.5;">${news.description}</p>
    <p style="margin: 10px 0 0 0; font-size: 12px; color: #94a3b8;">${news.pubDate}</p>
  </div>
  `;
});

emailHtml += `
  <p style="margin-top: 30px; padding-top: 20px; border-top: 1px solid #e2e8f0; color: #94a3b8; font-size: 12px;">
    Sent via n8n workflow • Created by Claude
  </p>
</div>
`;

return [{
  json: {
    emailSubject: `🤖 AI News Digest - ${today}`,
    emailHtml: emailHtml,
    newsCount: newsItems.length
  }
}];
