const text = $input.item.json.message.text;
const match = text.match(/DONE\s+(\d+)/i);
const taskId = match ? match[1] : null;
return { json: { taskId: taskId, chatId: $input.item.json.message.chat.id } };

const items = $input.all();

return items.map(item => {
  const task = item.json;
  const now = new Date();
  const dueDate = new Date(task['Due Date']);
  const lastReminder = task['Last Reminder Sent'] ? new Date(task['Last Reminder Sent']) : null;
  const reminderCount = parseInt(task['Reminder Count']) || 0;

  const hoursUntilDue = (dueDate - now) / (1000 * 60 * 60);
  const isOverdue = hoursUntilDue < 0;

  let urgencyLevel = 'normal';
  if (isOverdue) {
    urgencyLevel = 'critical';
  } else if (reminderCount >= 4) {
    urgencyLevel = 'warning';
  } else if (reminderCount >= 2) {
    urgencyLevel = 'warning';
  }

  let priorityEmoji = '🟢';
  if (task.Priority === 'High') priorityEmoji = '🔴';
  else if (task.Priority === 'Medium') priorityEmoji = '🟠';

  return {
    json: {
      ...task,
      hoursUntilDue: Math.round(hoursUntilDue * 10) / 10,
      isOverdue: isOverdue,
      urgencyLevel: urgencyLevel,
      priorityEmoji: priorityEmoji,
      reminderCount: reminderCount
    }
  };
});

