parameters.jsCode:
// Get all input items
const items = $input.all();

// Process each item
return items.map(item => {
  const phoneNumber = item.json['Phone Number'];

  // Convert to string and remove all non-digit characters
  let cleanNumber = String(phoneNumber).replace(/\D/g, '');

  // Handle different cases
  let standardizedNumber;

  if (cleanNumber.length === 10) {
    // Perfect - just 10 digits
    standardizedNumber = cleanNumber;
  } else if (cleanNumber.length === 11 && cleanNumber.startsWith('1')) {
    // US number with country code +1
    standardizedNumber = cleanNumber.slice(1);
  } else {
    // Wrong format - too many, too few, or wrong country code
    standardizedNumber = 'incorrect format';
  }

  // Return the item with standardized phone number
  return {
    json: {
      ...item.json,
      'Phone Number': standardizedNumber
    }
  };
});

