  "parameters": {
    "promptType": "define",
    "text": "={{ $json.userMessage }}",
    "options": {
      "systemMessage": "You are a helpful assistant."
    }
  },
  "type": "@n8n/n8n-nodes-langchain.agent",
  "typeVersion": 2,
  "position": [2200, 0],
  "id": "uuid-here",
