  "parameters": {
    "content": "## 📌 Section Title\n\nDescription here.\n\n**Setup:**\n- Step 1\n- Step 2",
    "height": 200,
    "width": 300,
    "color": 5
  },
  "type": "n8n-nodes-base.stickyNote",
