// Extract and validate JSON from AI output
const output = $input.first().json.output || $input.first().json.text || '';

let jsonString = output;

// Try to extract JSON if wrapped in markdown or has extra text
const jsonMatch = output.match(/\{[\s\S]*\}/);
if (jsonMatch) {
  jsonString = jsonMatch[0];
}

// Clean common issues
jsonString = jsonString
  .replace(/^```json\s*/i, '')
  .replace(/^```\s*/i, '')
  .replace(/\s*```$/i, '')
  .replace(/,\s*([}\]])/g, '$1') // Remove trailing commas
  .trim();

let workflow = null;
let isValid = false;
let error = '';

try {
  workflow = JSON.parse(jsonString);

  // Validate required fields
  if (!workflow.name) {
    workflow.name = 'Generated Workflow';
  }

  if (!workflow.nodes || !Array.isArray(workflow.nodes)) {
    throw new Error('Missing or invalid nodes array');
  }

  if (!workflow.connections || typeof workflow.connections !== 'object') {
    workflow.connections = {};
  }

  // Ensure settings exist
  if (!workflow.settings) {
    workflow.settings = {
      executionOrder: 'v1',
      saveManualExecutions: true,
      saveDataSuccessExecution: 'all',
      saveExecutionProgress: true,
      saveDataErrorExecution: 'all',
      timezone: 'Asia/Kolkata'
    };
  }

  // Ensure other required fields
  if (!workflow.staticData) workflow.staticData = null;
  if (!workflow.pinData) workflow.pinData = {};

  // Validate each node has required fields
  for (let i = 0; i < workflow.nodes.length; i++) {
    const node = workflow.nodes[i];
    if (!node.id) {
      node.id = 'node-' + Math.random().toString(36).substr(2, 9);
    }
    if (!node.name) {
      node.name = 'Node ' + (i + 1);
    }
    if (!node.position) {
      node.position = [i * 220, 0];
    }
    if (!node.parameters) {
      node.parameters = {};
    }
  }

  isValid = true;

} catch (e) {
  error = e.message;
  isValid = false;
}

return {
  json: {
    isValid: isValid,
    error: error,
    workflow: workflow,
    raw_output: output.substring(0, 500) + '...'
  }
};

// Format success response
const workflow = $input.first().json;
const workflowData = $('Validate & Clean JSON').first().json.workflow;

const nodeCount = workflowData?.nodes?.length || 0;
const stickyCount = workflowData?.nodes?.filter(n => n.type === 'n8n-nodes-base.stickyNote').length || 0;
const triggerNode = workflowData?.nodes?.find(n => n.type?.includes('Trigger') || n.type?.includes('trigger') || n.type?.includes('webhook'));

const response = `✅ **Workflow Created Successfully!**

📋 **Name:** ${workflowData?.name || 'Workflow'}
🔗 **Link:** https://n8n.aiwithdhruv.cloud/workflow/${workflow.id}
📊 **Nodes:** ${nodeCount} (${stickyCount} sticky notes)
⚡ **Trigger:** ${triggerNode?.name || 'Manual'}

**Next Steps:**
1. Open the workflow link above
2. Configure any credentials marked with "YOUR_CREDENTIAL_ID"
3. Update placeholder values (SHEET_ID, etc.)
4. Test the workflow
5. Activate when ready!

_Workflow ID: ${workflow.id}_`;

return {
  json: {
    response: response,
    workflowId: workflow.id,
    workflowName: workflowData?.name,
    workflowUrl: `https://n8n.aiwithdhruv.cloud/workflow/${workflow.id}`,
    nodeCount: nodeCount,
    success: true
  }
};

// Format error response
const data = $input.first().json;

const response = `❌ **Workflow Generation Failed**

**Error:** ${data.error || 'Invalid JSON structure'}

**Possible Causes:**
- Complex request that confused the AI
- JSON syntax error in generation
- Missing required fields

**Please try:**
1. Simplify your request
2. Be more specific about what you need
3. Break complex workflows into steps

**Debug Info:**
\`\`\`
${data.raw_output?.substring(0, 200) || 'No output captured'}...
\`\`\``;

return {
  json: {
    response: response,
    success: false,
    error: data.error
  }
};
